function sendToSearch(a){window.location.href="/busqueda/texto/"+encodeURI(a.toLowerCase().split(" ").join("-"))}$(document).ready(function(){$("[data-search-btn]").click(function(){var a=$("[data-search-text]").val();sendToSearch(a)}),$("[data-search-text]").keypress(function(a){var e=a.which;if(13==e){var t=$("[data-search-text]").val();sendToSearch(t)}})});